
public class CircleStat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int Radius = 13; // reference variable for the radius
		double CirofCircle; // reference variable for equation of circumference
		double AreaofCircle; // reference variable for equation of area

		CirofCircle = 2 * 3.14 * Radius;
		AreaofCircle = 3.14 * (Radius * Radius);

		System.out.println("When the radius is " + Radius + " the circumference" + " is " + CirofCircle
				+ " and the area is " + AreaofCircle);

	}

}
